// SPDX-License-Identifier: MIT
// HRC721 Burnable Extension based on OpenZeppelin ERC721Burnable

pragma solidity ^0.8.24;

import { Context } from '../../utils/Context.sol';
import { HRC721 } from '../HRC721.sol';

/**
 * @title HRC721 Burnable Token
 * @dev HRC721 Token that can be burned (destroyed).
 * Based on ERC-721 standard but follows HRC naming convention.
 */
abstract contract HRC721Burnable is Context, HRC721 {
    /**
     * @dev Burns `tokenId`. See {ERC721-_burn}.
     *
     * Requirements:
     *
     * - The caller must own `tokenId` or be an approved operator.
     */
    function burn(uint256 tokenId) public virtual {
        // Setting an "auth" arguments enables the `_isAuthorized` check which verifies that the token exists
        // (from != 0). Therefore, it is not needed to verify that the return value is not 0 here.
        _update(address(0), tokenId, _msgSender());
    }
}

