// HRC721Utils Library based on OpenZeppelin ERC721Utils

pragma solidity ^0.8.24;

import { IHRC721Receiver } from '../interfaces/IHRC721Receiver.sol';
import { IHRC721Errors } from '../interfaces/IHRC721Errors.sol';

/**
 * @dev Library of standard common utility functions for HRC721 contracts.
 * Based on ERC-721 standard but follows HRC naming convention.
 */
library HRC721Utils {
    /**
     * @dev Performs an acceptance check for the provided `operator` by calling {IHRC721Receiver-onERC721Received}
     * on the `to` address. The `operator` is generally the address that initiated the token transfer (i.e. `msg.sender`).
     *
     * The acceptance call is not executed and treated as a no-op if the target address doesn't contain code (i.e. an EOA).
     * Otherwise, the recipient must implement {IHRC721Receiver-onERC721Received} and return the acceptance magic value to accept
     * the transfer.
     */
    function checkOnERC721Received(
        address operator,
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) internal {
        if (to.code.length > 0) {
            try IHRC721Receiver(to).onERC721Received(operator, from, tokenId, data) returns (bytes4 retval) {
                if (retval != IHRC721Receiver.onERC721Received.selector) {
                    // Token rejected
                    revert IHRC721Errors.ERC721InvalidReceiver(to);
                }
            } catch (bytes memory reason) {
                if (reason.length == 0) {
                    // non-IHRC721Receiver implementer
                    revert IHRC721Errors.ERC721InvalidReceiver(to);
                } else {
                    assembly ('memory-safe') {
                        revert(add(reason, 0x20), mload(reason))
                    }
                }
            }
        }
    }
}

