import { Pausable } from '../utils/Pausable.sol';

contract PausableTest is Pausable {
    address private _owner;

    constructor() {
        _owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == _owner, 'Not owner');
        _;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function testWhenNotPaused() public view whenNotPaused returns (bool) {
        return true;
    }

    function testWhenPaused() public view whenPaused returns (bool) {
        return true;
    }
}

